import axios from 'axios';
import { SERVER_URL, NODE_ENV } from '../utils/config';
// import { history } from '../../index';

const tuesdayApi = function (address) {

  const headers = {
    'Content-Type': 'application/json',
    'Cache-Control': 'no-cache',
  };

  const r = (url, extra = {}) => {
    // проверка на прод или дев, корректирует запросы
    if (NODE_ENV !== 'development') {
      axios.defaults.baseURL = extra && extra.baseUrl ? extra.baseUrl : `${SERVER_URL}/api`;
    } else {
      axios.defaults.baseURL = extra && extra.baseUrl ? extra.baseUrl : '/api';
    }

    localStorage.getItem('token') ? headers['x-access-token'] = `${localStorage.getItem('token')}` : headers['x-access-token'] = null;
    extra.headers = extra.headers ? extra.headers : {};

    return new Promise((resolve, reject) => {
      axios({
        url,
        // headers,
        ...extra,
        headers: {
          ...headers,
          ...extra.headers,
        },
        timeout: 60000,
      })
        .then((response) => {
          resolve(response.data);
        })
        .catch((err) => {
          if (!err.response) {
            reject(err);
          } else {
            if (401 === err.response.status) {

              localStorage.removeItem('token');
              // history.push('/login');

            } else if (403 === err.response.status) {
              // history.push('/access-denied');
            }
            reject(err);
          }
        });
    });
  };

  return {
    auth: {
      getToken: (username, password) => r('/token/obtain/',
        {
          method: 'post',
          data: {
            username,
            password,
          },
        }),
      changePassword: (userId, data) => r(`/users/${userId}/password/`, {
        method: 'post',
        data,
      }),
      register: data => r('/register', {
        method: 'post',
        data,
      }),
      login: data => r('/login', {
        method: 'post',
        data,
      }),
      currentUser: () => r('/me', {
        method: 'get',
      }),
    },
    utils: {
      clientPhoto: formData => r('/utils/photos/',
        {
          method: 'post',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          data: formData,
        }),
      getPhoto: formData => r('/imgread',
        {
          method: 'get',
        }),
      createAttachment: (formData, type) => r(`/attachments/${type}`,
        {
          method: 'post',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          data: formData,
        }),
      getAttachment: id => r(`/attachments/${id}`,
        {
          method: 'get',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        }),
    },
    roles: {
      getAll: () => r('/users/roles/',
        {
          method: 'get',
        }),
      getRole: roleId => r(`/users/roles/${roleId}`,
        {
          method: 'get',
        }),
      createRole: data => r('/users/roles/',
        {
          method: 'post',
          data,
        }),
      editRole: (id, data) => r(`/users/roles/${id}/`,
        {
          method: 'put',
          data,
        }),
      deleteRole: id => r(`/users/roles/${id}`,
        {
          method: 'delete',
        }),
    },
    users: {
      getAll: () => r('/users',
        {
          method: 'get',
        }),
      createUser: data => r('/users/',
        {
          method: 'post',
          data,
        }),
      getUser: id => r(`/users/${id}/`,
        {
          method: 'get',
        }),
      editUser: (id, data) => r(`/users/${id}/`,
        {
          method: 'put',
          data,
        }),
      deleteUser: id => r(`/users/${id}/`,
        {
          method: 'delete',
        }),
    },
    articles: {
      getAll: (queryParams = { page: 1, size: 100 }) => r('/articles',
        {
          method: 'get',
          params: {
            ...queryParams,
          },
        }),
      getOne: id => r(`/article/${id}`,
        {
          method: 'get',
        }),
      create: data => r('/article',
        {
          method: 'post',
          data,
        }),
      delete: id => r(`/article/${id}`,
        {
          method: 'delete',
        }),
      patch: (id, data) => r(`/article/${id}`,
        {
          method: 'patch',
          data,
        }),
    },
    articeCategories: {
      getAll: () => r('articles/categories',
        {
          method: 'get',
        }),
      deleteOne: id => r(`articles/categories/${id}`,
        {
          method: 'delete',
        }),
      getOne: id => r(`/articles/categories/${id}`,
        {
          method: 'get',
        }),
      patch: (id, data) => r(`/articles/categories/${id}`,
        {
          method: 'patch',
          data,
        }),
    },
    tags: {
      getAll: () => r('tags', {
        method: 'get',
      }),
      create: data => r('tags', {
        method: 'post',
        data,
      }),
      patch: (id, data) => r(`/tags/${id}`,
        {
          method: 'patch',
          data,
        }),
      deleteOne: id => r(`tags/${id}`,
        {
          method: 'delete',
        }),
    },
  };
};


const api = new tuesdayApi();
export default api;
